mapper.select:

select *
from
(
SELECT rownum as rno, lecture_notice_seq AS seq, l_title AS title, writeday, viewcount
FROM tbl_lecture_notice
WHERE fk_course_seq = #{fk_course_seq}

	and lower(l_title) like '%'||lower(#{searchWord})||'%'

)V
where V.rno BETWEEN #{startRno} AND #{endRno}
order by writeday desc

SELECT previousseq, previoussubject, lecture_notice_seq as seq, l_title as title, l_content as content , viewcount, writeday , nextseq, nextsubject from ( select lag (lecture_notice_seq) over(order by lecture_notice_seq desc) AS previousseq , lag (l_title,1) over(order by lecture_notice_seq desc) AS previoussubject , lecture_notice_seq , lead (lecture_notice_seq) over(order by lecture_notice_seq desc) AS nextseq , lead (l_title, 1) over(order by lecture_notice_seq desc) AS Nextsubject , writeday, viewcount, l_title, l_content from tbl_lecture_notice
where lower(l_title) like '%' || lower(#{searchWord}) || '%'
) V WHERE V.lecture_notice_seq = #{seq}
