mapper.update:
UPDATE tbl_course
SET register_count = register_count + #{increment}
WHERE course_seq = #{course_seq}

mapper.select:
select v0.curriculum_seq as curriculum_seq, v0.fk_department_seq as curriculum_fk_department_seq, v0.grade as curriculum_grade, v0.name as curriculum_name, v0.credit as curriculum_credit, v0.required as curriculum_required, v0.exist as curriculum_exist, v1.course_seq as course_seq, v1.fk_professor_id as course_fk_professor_id, v1.fk_curriculum_seq as course_fk_curriculum_seq, v1.capacity as course_capacity, v1.semester_date as course_semester_date, v1.exist as course_exist, v1.register_count as course_register_count, v2.prof_id as professor_id, v2.pwd as professor_pwd, v2.name as professor_name, v2.jubun as professor_jubun, v2.tel as professor_tel, v2.department_seq as professor_department_seq, v2.email as professor_email, v2.office_address as professor_office_address, v2.employment_stat as professor_employment_stat, v2.employment_date as professor_employment_date, v2.img_name as professor_img_name from tbl_curriculum v0 join tbl_course v1 on v0.curriculum_seq = v1.fk_curriculum_seq join tbl_professor v2 on v1.fk_professor_id = v2.prof_id
v1.exist = 1
and v0.fk_department_seq = #{department_seq}
and v0.fk_department_seq IS NULL
and v0.grade = #{grade}

mapper.update:
UPDATE tbl_course
SET exist = 0
where course_seq = #{course_seq}

UPDATE tbl_course
SET capacity = #{capacity}
where course_seq = #{course_seq}
