select.if:
select STUDENT_ID
from tbl_student
where name = #{name} and email = #{email}

select PROF_ID
from tbl_professor
where name = #{name} and email = #{email}

select ADMIN_ID
from tbl_admin
where name = #{name} and email = #{email}

select STUDENT_ID, email
from tbl_student
where student_id = #{id} and email = #{email}

select PROF_ID, email
from tbl_professor
where PROF_ID = #{id} and email = #{email}

select ADMIN_ID, email
from tbl_admin
where ADMIN_ID = #{id} and email = #{email}
