mapper.select:
select * from ( select rownum rn, lecture_notice_seq AS seq, l_title AS title, writeday, viewcount from ( SELECT * FROM tbl_lecture_notice WHERE fk_course_seq = #{fk_course_seq}
and lower(l_title) like '%'||lower(#{searchWord})||'%'
order by writeday desc )V )T where rn BETWEEN #{startRno} AND #{endRno}

SELECT previousseq, previoussubject, lecture_notice_seq as seq, l_title as title, l_content as content , viewcount, writeday , nextseq, nextsubject from ( select lag (lecture_notice_seq) over(order by lecture_notice_seq desc) AS previousseq , lag (l_title,1) over(order by lecture_notice_seq desc) AS previoussubject , lecture_notice_seq , lead (lecture_notice_seq) over(order by lecture_notice_seq desc) AS nextseq , lead (l_title, 1) over(order by lecture_notice_seq desc) AS Nextsubject , writeday, viewcount, l_title, l_content from tbl_lecture_notice where fk_course_seq = #{fk_course_seq}
where lower(l_title) like '%' || lower(#{searchWord}) || '%'
) V WHERE V.lecture_notice_seq = #{seq}

SELECT lecture_notice_seq AS seq, l_title AS title, writeday, viewcount
FROM tbl_lecture_notice
where status = 1 and fk_course_seq = #{fk_course_seq}

select announcement_seq, a_title, writeday, viewcount from ( select rownum rn, announcement_seq, a_title, a_content, attatched_file, writeday, viewcount, status, orgfilename from ( SELECT * FROM tbl_announcement
where lower(a_title) like '%'||lower(#{searchWord})||'%'
order by writeday desc )V )T where rn BETWEEN #{startRno} AND #{endRno}

SELECT previousseq, previoussubject, announcement_seq, a_title, a_content , viewcount, writeday , nextseq, nextsubject , attatched_file, orgfilename from ( select lag (announcement_seq) over(order by announcement_seq desc) AS previousseq , lag (a_title,1) over(order by announcement_seq desc) AS previoussubject , announcement_seq , lead (announcement_seq) over(order by announcement_seq desc) AS nextseq , lead (a_title, 1) over(order by announcement_seq desc) AS Nextsubject , attatched_file, writeday, viewcount, a_title, a_content, orgfilename from tbl_announcement
where lower(a_title) like '%' || lower(#{searchWord}) || '%'
) V WHERE V.announcement_seq = #{seq}
